function showBigNotes() {
    //Sfondo ingrandito
	var message = document.getElementById("newmessage");
	if(message) {
	    message.style.background = "url(data:image/jpeg;base64,)";
	    message.style.height = "428px";
	    //Trovo lo sfondo delle note
	    var divs = XQuery(".//div[@class='newmessagediv']");
	    if (divs.snapshotLength > 0) {
	        var myDiv = divs.snapshotItem(0);
	        //Differenzio per le versioni RTL
	        if(isArabo) {
	            myDiv.style.right = "48px";
	            myDiv.style.top = "45px";
	            var spans = XQuery(".//div[@class='newmessagediv']/span");
	            if(spans.snapshotLength > 0) {
	                spans.snapshotItem(0).style.margin = "";
	            }
	        } else {
                myDiv.style.left = "40px";
	            myDiv.style.top = "45px";
	        }
	    }
	    //Trovo il campo note
	    var textareas = XQuery(".//textarea[@name='memo']");
	    if (textareas.snapshotLength > 0) {
	        var myTextarea = textareas.snapshotItem(0);
	        myTextarea.cols = 52;
	    }
    }
}

function showBigWriteMessage() {
    //Sfondo ingrandito
	var message = document.getElementById("newmessage");
	if(message) {
	    message.style.background = "url(data:image/jpeg;base64,)";
	    message.style.height = "428px";
	    //Trovo lo sfondo delle note
	    var tables = XQuery(".//table[@class='message_table']");
	    if (tables.snapshotLength > 0) {
	        var myTable = tables.snapshotItem(0);
	        //Differenzio per le versioni RTL
	        if(isArabo) {
	            myTable.style.right = "48px";
	            myTable.style.top = "45px";
	        } else {
                myTable.style.left = "44px";
	            myTable.style.top = "44px";
	        }
	    }
	    //Trovo il campio testo
        var textareas = XQuery(".//textarea[@name='text']");
	    if (textareas.snapshotLength > 0)
	    {
	        var myTextarea = textareas.snapshotItem(0);
	        myTextarea.cols = 50;
	        myTextarea.rows = 8;
	    }
    }
}